import {useState} from 'react'
import "../../styles/signinPage.css"
import axios from 'axios';

const Signin = () => {
  const [email, setEmail] = useState()
  const [password, setPassword] = useState()

  const handleSubmit = async (e) => {
    e.preventDefault();
    let formData = {email,password}
    console.log('hello')
    try {
      console.log('here ')
      let response = await axios.post('https://localhost:3001/signIn', formData)
      if (response.data) {
        console.log(response)
        localStorage.setItem('token', response.data.token.JWT)
      } else {
        alert("Email and/or password is incorrect")
      }
    } catch (error) {
      console.log(error)
    }

  }
  return (
    <div className="container">
        <label htmlFor="email"><b>Email</b></label>
        <input type="email" placeholder="Enter Email" name="email" required onChange={(e)=>setEmail(e.target.value)}/>

        <label htmlFor="password"><b>Password</b></label>
        <input type="password" placeholder="Enter Password" name="password" required onChange={(e)=>setPassword(e.target.value)}/>

        <button type="submit" onClick={(e)=>handleSubmit(e)}>Login</button>
    </div>
)

}

export default Signin
